mapper.select:
    	select count(*) 
    	from carpool 
    	
    		
    		
    		
    		  			
    		
    		
			    cp_depart like #{find_name}	
			
			    		  			
			
			
			    cp_arrive like #{find_name} 
			
    		and cp_valid = 0

    	select *  
    	from (select rownum rnum, cp_num, cp_id, cp_depart, 
		             cp_arrive, cp_time,  cp_regdate, cp_sum, cp_distance  
    		  from (select * from carpool 
    		  		
    		  		
    		  			
    		  			
    		  			
    		  			
    		  			
			    		
			    			cp_depart like #{find_name}	
			    		
			    		  			
			    		
			    		
			    		  	cp_arrive like #{find_name} 
			    		
			    		and cp_valid = 0 
    		  		
    		  		order by #{type} desc)
    		  	) 
    	  where rnum >= ((#{page}-1) * 10 + 1) 
    	  and rnum <= (#{page} * 10)

mapper.update:
update carpool 
set cp_valid = 1 
where cp_num = #{param}
